import { apiRequest } from "./queryClient";
import type { QuoteRequest } from "@shared/schema";

export const shippingApi = {
  getQuote: async (data: QuoteRequest) => {
    const response = await apiRequest("POST", "/api/quote", data);
    return response.json();
  },

  generateInvoice: async (quoteData: any) => {
    const response = await apiRequest("POST", "/api/invoice", quoteData);
    return response.json();
  },

  getInvoice: async (id: string) => {
    const response = await apiRequest("GET", `/api/invoice/${id}`);
    return response.json();
  },

  downloadInvoicePDF: async (id: string) => {
    const response = await fetch(`/api/invoice/${id}/pdf`);
    if (!response.ok) {
      throw new Error("Failed to download PDF");
    }
    return response.blob();
  }
};

