import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { Download, Printer, Mail, ArrowLeft } from "lucide-react";
import type { Invoice } from "@shared/schema";

export default function Invoice() {
  const [match, params] = useRoute("/invoice/:id");
  const { toast } = useToast();

  const { data: invoice, isLoading, error } = useQuery<Invoice>({
    queryKey: ['/api/invoice', params?.id],
    enabled: !!params?.id
  });

  const handleDownloadPDF = async () => {
    try {
      const response = await fetch(`/api/invoice/${params?.id}/pdf`);
      if (!response.ok) throw new Error('Failed to download PDF');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `invoice-${invoice?.number}.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);

      toast({
        title: "PDF Downloaded",
        description: "Your invoice PDF has been downloaded successfully.",
      });
    } catch (error) {
      toast({
        variant: "destructive",
        title: "Download Failed",
        description: "Failed to download PDF. Please try again.",
      });
    }
  };

  const handlePrint = () => {
    window.print();
  };

  const handleGoBack = () => {
    window.history.back();
  };

  const getServiceDisplayName = (serviceType: string) => {
    const serviceMap = {
      economy: "Economy (5-7 business days)",
      standard: "Standard (3-5 business days)", 
      express: "Express (1-2 business days)",
      overnight: "Overnight"
    };
    return serviceMap[serviceType as keyof typeof serviceMap] || serviceType;
  };

  if (!match || !params?.id) {
    return <div>Invoice not found</div>;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="max-w-4xl mx-auto px-4 py-8">
          <Skeleton className="h-8 w-48 mb-8" />
          <Card>
            <CardContent className="p-8 space-y-6">
              <Skeleton className="h-20 w-full" />
              <Skeleton className="h-32 w-full" />
              <Skeleton className="h-48 w-full" />
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (error || !invoice) {
    return (
