import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form } from "@/components/ui/form";
import { Progress } from "@/components/ui/progress";
import { ChevronLeft, ChevronRight, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import StepSender from "./steps/step-sender";
import StepRecipient from "./steps/step-recipient";
import StepPackage from "./steps/step-package";
import StepQuote from "./steps/step-quote";
import { multiStepFormSchema, type MultiStepForm, type ShippingQuoteResponse } from "@shared/schema";

interface MultiStepFormProps {
  onOrderComplete: (order: any) => void;
}

type StepType = 'sender' | 'recipient' | 'package' | 'quote';

const steps: { id: StepType; title: string; description: string }[] = [
  { id: 'sender', title: 'Sender Information', description: 'From address details' },
  { id: 'recipient', title: 'Recipient Information', description: 'To address details' },
  { id: 'package', title: 'Package Details', description: 'Weight, dimensions & value' },
  { id: 'quote', title: 'Quote Results', description: 'Select service & confirm order' }
];

export default function MultiStepForm({ onOrderComplete }: MultiStepFormProps) {
  const [currentStep, setCurrentStep] = useState<StepType>('sender');
  const [quoteData, setQuoteData] = useState<ShippingQuoteResponse | null>(null);
  const [isLoadingQuote, setIsLoadingQuote] = useState(false);
  const { toast } = useToast();

  const form = useForm<MultiStepForm>({
    resolver: zodResolver(multiStepFormSchema),
    defaultValues: {
      sender: {
        fullName: "",
        company: "",
        phone: "",
        email: "",
        address1: "",
        address2: "",
        city: "",
        province: "",
        postalCode: "",
        country: "CA"
      },
      recipient: {
        fullName: "",
        company: "",
        phone: "",
        email: "",
        address1: "",
        address2: "",
        city: "",
        province: "",
        postalCode: "",
        country: "US"
      },
      package: {
        length: 10,
        width: 10,
        height: 10,
        weight: 1,
        value: 100
      }
    }
  });

  const getCurrentStepIndex = () => steps.findIndex(step => step.id === currentStep);
  const progress = ((getCurrentStepIndex() + 1) / steps.length) * 100;

  // Validation helper
  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePostalCode = (postalCode: string, country: string): boolean => {
    if (country === "CA") {
      // Canadian postal code: A1A 1A1
      const canadianRegex = /^[A-Za-z]\d[A-Za-z][ -]?\d[A-Za-z]\d$/;
      return canadianRegex.test(postalCode);
    } else if (country === "US") {
      // US ZIP code: 12345 or 12345-6789
      const usRegex = /^\d{5}(-\d{4})?$/;
      return usRegex.test(postalCode);
    }
    return true;
  };

  const canGoNext = () => {
    const stepIndex = getCurrentStepIndex();

    if (stepIndex === 0) {
      // Validate sender step
      const senderData = form.getValues('sender');

      // Check all required fields are filled
      const requiredFields = [
        senderData.fullName,
        senderData.email,
        senderData.phone,
        senderData.address1,
        senderData.city,
        senderData.province,
        senderData.postalCode
      ];

      const allFieldsFilled = requiredFields.every(field => field && field.trim().length > 0);

      if (!allFieldsFilled) return false;

      // Validate email format
      if (!validateEmail(senderData.email)) return false;

      // Validate postal code format
      if (!validatePostalCode(senderData.postalCode, senderData.country)) return false;

      return true;
    }

    if (stepIndex === 1) {
      // Validate recipient step
      const recipientData = form.getValues('recipient');

      const requiredFields = [
        recipientData.fullName,
        recipientData.email,
        recipientData.phone,
        recipientData.address1,
        recipientData.city,
        recipientData.province,
        recipientData.postalCode
      ];

      const allFieldsFilled = requiredFields.every(field => field && field.trim().length > 0);

      if (!allFieldsFilled) return false;

      if (!validateEmail(recipientData.email)) return false;
      if (!validatePostalCode(recipientData.postalCode, recipientData.country)) return false;

      return true;
    }

    if (stepIndex === 2) {
      // Validate package step
      const packageData = form.getValues('package');
      return packageData.weight > 0 && 
             packageData.length > 0 && 
             packageData.width > 0 && 
             packageData.height > 0 &&
             packageData.value > 0;
    }

    return false;
  };

  const handleNext = async () => {
    const stepIndex = getCurrentStepIndex();

    if (!canGoNext()) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields correctly before proceeding.",
        variant: "destructive",
      });
      return;
    }

    if (stepIndex < steps.length - 1) {
      const nextStep = steps[stepIndex + 1].id;
      setCurrentStep(nextStep);

      // If moving to quote step, fetch quotes
      if (nextStep === 'quote' && !quoteData) {
        await handleGetQuote();
      }
    }
  };

  const handlePrevious = () => {
    const stepIndex = getCurrentStepIndex();
    if (stepIndex > 0) {
      setCurrentStep(steps[stepIndex - 1].id);
    }
  };

  const handleGetQuote = async () => {
    setIsLoadingQuote(true);
    const formData = form.getValues();

    const quoteRequest = {
      origin: {
        country: formData.sender.country,
        postalCode: formData.sender.postalCode,
        city: formData.sender.city,
        province: formData.sender.province
      },
      destination: {
        country: formData.recipient.country,
        postalCode: formData.recipient.postalCode,
        city: formData.recipient.city,
        province: formData.recipient.province
      },
      package: formData.package
    };

    try {
      const response = await fetch('/api/quote', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(quoteRequest)
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.message || `Server error: ${response.status}`);
      }

      const quote = await response.json();

      // Safely handle the response
      if (!quote || typeof quote !== 'object') {
        throw new Error('Invalid response from server');
      }

      // Ensure services array exists and has proper structure
      const safeQuote: ShippingQuoteResponse = {
        currency: quote.currency || "CAD",
        services: Array.isArray(quote.services) ? quote.services : []
      };

      setQuoteData(safeQuote);

      // Show warning if no services available
      if (safeQuote.services.length === 0) {
        toast({
          title: "No Shipping Rates Available",
          description: "Unfortunately, no shipping rates are available for this route. Please verify your addresses and try again.",
          variant: "destructive",
        });
      }

    } catch (error) {
      console.error('Quote error:', error);

      toast({
        title: "Quote Error",
        description: error instanceof Error 
          ? error.message 
          : "Failed to get shipping quote. Please check your information and try again.",
        variant: "destructive",
      });

      // Set empty result to show error state
      setQuoteData({ currency: "CAD", services: [] });
    } finally {
      setIsLoadingQuote(false);
    }
  };

  const copyFromSender = () => {
    const senderData = form.getValues('sender');
    form.setValue('recipient', { 
      ...senderData,
      country: senderData.country // Keep the country as-is or change to US if needed
    });

    toast({
      title: "Information Copied",
      description: "Sender information has been copied to recipient fields.",
    });
  };

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 'sender':
        return <StepSender form={form} />;
      case 'recipient':
        return <StepRecipient form={form} copyFromSender={copyFromSender} />;
      case 'package':
        return <StepPackage form={form} />;
      case 'quote':
        return (
          <StepQuote 
            form={form} 
            quoteData={quoteData}
            isLoading={isLoadingQuote}
            onOrderComplete={onOrderComplete}
            onRetry={handleGetQuote}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between text-xs sm:text-sm text-gray-600 mb-2">
          {steps.map((step, index) => (
            <span 
              key={step.id} 
              className={`${
                index <= getCurrentStepIndex() 
                  ? 'text-blue-600 font-semibold' 
                  : 'text-gray-400'
              } transition-colors duration-200`}
            >
              <span className="hidden sm:inline">{step.title}</span>
              <span className="sm:hidden">Step {index + 1}</span>
            </span>
          ))}
        </div>
        <Progress value={progress} className="h-2" />
      </div>

      <Card className="shadow-lg">
        <CardHeader>
          <CardTitle className="flex items-center gap-3">
            <span className="bg-blue-600 text-white rounded-full w-10 h-10 flex items-center justify-center text-sm font-bold shrink-0">
              {getCurrentStepIndex() + 1}
            </span>
            <div>
              <div className="text-xl">{steps[getCurrentStepIndex()]?.title}</div>
              <CardDescription className="text-sm mt-1">
                {steps[getCurrentStepIndex()]?.description}
              </CardDescription>
            </div>
          </CardTitle>
        </CardHeader>

        <CardContent>
          <Form {...form}>
            <form className="space-y-6">
              {renderCurrentStep()}
            </form>
          </Form>

          {/* Navigation Buttons */}
          <div className="flex justify-between mt-8 pt-6 border-t">
            <Button
              type="button"
              variant="outline"
              onClick={handlePrevious}
              disabled={getCurrentStepIndex() === 0}
              data-testid="button-previous"
            >
              <ChevronLeft className="mr-2 h-4 w-4" />
              Previous
            </Button>

            {currentStep !== 'quote' && (
              <Button
                type="button"
                onClick={handleNext}
                disabled={!canGoNext() || isLoadingQuote}
                data-testid="button-next"
              >
                {isLoadingQuote ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Loading...
                  </>
                ) : (
                  <>
                    Next
                    <ChevronRight className="ml-2 h-4 w-4" />
                  </>
                )}
              </Button>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/steps/step-quote.t.content:
import { useState } from "react";
import { UseFormReturn } from "react-hook-form";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Loader2, Package, Truck, Clock, DollarSign, CheckCircle2, AlertCircle, RefreshCw } from "lucide-react";
import type { ShippingQuoteResponse } from "@shared/schema";

interface StepQuoteProps {
  form: UseFormReturn<any>;
  quoteData: ShippingQuoteResponse | null;
  isLoading: boolean;
  onOrderComplete: (order: any) => void;
  onRetry: () => void;
}

export default function StepQuote({ 
  form, 
  quoteData, 
  isLoading, 
  onOrderComplete,
  onRetry 
}: StepQuoteProps) {
  const [selectedService, setSelectedService] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSelectService = (serviceId: string) => {
    setSelectedService(serviceId);
  };

  const handleConfirmOrder = async () => {
    if (!selectedService || !quoteData) return;

    setIsSubmitting(true);

    const formData = form.getValues();
    const selectedServiceData = quoteData.services.find(s => s.id === selectedService);

    const orderPayload = {
      sender: formData.sender,
      recipient: formData.recipient,
      package: formData.package,
      service: selectedServiceData,
      totalPrice: selectedServiceData?.price || 0,
      currency: quoteData.currency
    };

    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(orderPayload)
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.message || 'Failed to create order');
      }

      const order = await response.json();
      onOrderComplete(order);

    } catch (error) {
      console.error('Order creation error:', error);
      alert(error instanceof Error ? error.message : 'Failed to create order. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Loading state
  if (isLoading) {
    return (
      <div className="flex flex-col items-center justify-center py-12">
        <Loader2 className="h-12 w-12 animate-spin text-blue-600 mb-4" />
        <h3 className="text-lg font-semibold mb-2">Fetching Shipping Quotes...</h3>
        <p className="text-gray-500 text-center">
          Please wait while we calculate the best shipping rates for your package.
        </p>
      </div>
    );
  }

  // No quotes available
  if (!quoteData || !Array.isArray(quoteData.services) || quoteData.services.length === 0) {
    return (
      <div className="space-y-6">
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            <strong>No shipping rates available</strong>
            <p className="mt-2">
              Unfortunately, we couldn't find any shipping rates for this route. 
              This could be due to:
            </p>
            <ul className="list-disc list-inside mt-2 space-y-1">
              <li>Invalid postal/ZIP codes</li>
              <li>Package dimensions or weight exceeding carrier limits</li>
              <li>Restricted shipping route</li>
              <li>Temporary service unavailability</li>
            </ul>
          </AlertDescription>
        </Alert>

        <div className="flex justify-center">
          <Button onClick={onRetry} variant="outline" className="gap-2">
            <RefreshCw className="h-4 w-4" />
            Try Again
          </Button>
        </div>
      </div>
    );
  }

  // Display quotes
  return (
    <div className="space-y-6">
      <Alert className="bg-blue-50 border-blue-200">
        <Package className="h-4 w-4 text-blue-600" />
        <AlertDescription className="text-blue-800">
          Select your preferred shipping service below. Prices are in {quoteData.currency}.
        </AlertDescription>
      </Alert>

      {/* Service Cards */}
      <div className="grid grid-cols-1 gap-4">
        {quoteData.services.map((service) => (
          <Card
            key={service.id}
            className={`cursor-pointer transition-all hover:shadow-lg ${
              selectedService === service.id
                ? 'ring-2 ring-blue-600 border-blue-600'
                : 'hover:border-blue-300'
            }`}
            onClick={() => handleSelectService(service.id)}
          >
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Truck className="h-5 w-5 text-blue-600" />
                    {service.name}
                    {selectedService === service.id && (
                      <CheckCircle2 className="h-5 w-5 text-green-600 ml-auto" />
                    )}
                  </CardTitle>
                  <CardDescription className="mt-1">
                    {service.carrier || 'Standard Carrier'}
                  </CardDescription>
                </div>
                <div className="text-right ml-4">
                  <div className="text-2xl font-bold text-blue-600">
                    ${service.price.toFixed(2)}
                  </div>
                  <div className="text-xs text-gray-500">{quoteData.currency}</div>
                </div>
              </div>
            </CardHeader>

            <CardContent>
              <div className="flex flex-wrap gap-4 text-sm">
                {service.estimatedDays && (
                  <div className="flex items-center gap-2">
                    <Clock className="h-4 w-4 text-gray-400" />
                    <span className="text-gray-600">
                      Estimated: <strong>{service.estimatedDays} days</strong>
                    </span>
                  </div>
                )}

                {service.tracking !== false && (
                  <Badge variant="secondary" className="gap-1">
                    <Package className="h-3 w-3" />
                    Tracking Included
                  </Badge>
                )}

                {service.insurance && (
                  <Badge variant="secondary" className="gap-1">
                    <DollarSign className="h-3 w-3" />
                    Insurance Available
                  </Badge>
                )}
              </div>

              {service.description && (
                <p className="text-sm text-gray-600 mt-3">
                  {service.description}
                </p>
              )}
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Order Summary */}
      {selectedService && (
        <Card className="bg-gray-50 border-gray-300">
          <CardHeader>
            <CardTitle className="text-lg">Order Summary</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            {(() => {
              const selected = quoteData.services.find(s => s.id === selectedService);
              if (!selected) return null;

              return (
                <>
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Service:</span>
                    <span className="font-medium">{selected.name}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Carrier:</span>
                    <span className="font-medium">{selected.carrier || 'Standard'}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Estimated Delivery:</span>
                    <span className="font-medium">{selected.estimatedDays || 'N/A'} days</span>
                  </div>
                  <div className="border-t pt-3 flex justify-between">
                    <span className="font-semibold">Total:</span>
                    <span className="text-xl font-bold text-blue-600">
                      ${selected.price.toFixed(2)} {quoteData.currency}
                    </span>
                  </div>
                </>
              );
            })()}
          </CardContent>
        </Card>
      )}

      {/* Confirm Button */}
      <div className="flex justify-end gap-3 pt-4">
        <Button
          variant="outline"
          onClick={onRetry}
          disabled={isSubmitting}
          className="gap-2"
        >
          <RefreshCw className="h-4 w-4" />
          Recalculate
        </Button>

        <Button
          onClick={handleConfirmOrder}
          disabled={!selectedService || isSubmitting}
          className="gap-2 min-w-[150px]"
        >
          {isSubmitting ? (
            <>
              <Loader2 className="h-4 w-4 animate-spin" />
              Processing...
            </>
          ) : (
            <>
              <CheckCircle2 className="h-4 w-4" />
              Confirm Order
            </>
          )}
        </Button>
      </div>
    </div>
  );
}

