import axios from 'axios';

const POSTMARK_SERVER_TOKEN = process.env.POSTMARK_SERVER_TOKEN;

// Initialize Postmark
if (POSTMARK_SERVER_TOKEN) {
  console.log('âœ… Postmark initialized for direct API email delivery!');
} else {
  console.warn('âš ï¸  Postmark token not available');
}

interface EmailParams {
  to: string;
  from: string;
  subject: string;
  text?: string;
  html?: string;
  attachments?: Array<{
    content: string;
    filename: string;
    type: string;
    disposition: string;
  }>;
}

export async function sendEmail(params: EmailParams): Promise<boolean> {
  // Use direct Postmark API for all emails
  if (POSTMARK_SERVER_TOKEN) {
    try {
      const attachments = params.attachments?.map(att => ({
        Name: att.filename,
        Content: att.content,
        ContentType: att.type,
        ContentID: null
      }));

      const postmarkData = {
        From: params.from,
        To: params.to,
        Subject: params.subject,
        TextBody: params.text || '',
        HtmlBody: params.html || '',
        MessageStream: "outbound",
        ...(attachments && attachments.length > 0 && { Attachments: attachments })
      };

      const response = await axios.post(
        "https://api.postmarkapp.com/email",
        postmarkData,
        {
          headers: {
            "X-Postmark-Server-Token": POSTMARK_SERVER_TOKEN,
            "Content-Type": "application/json"
          }
        }
      );

      console.log(`âœ… Email sent successfully to ${params.to} via Postmark Direct API!`);
      console.log(`Message ID: ${response.data.MessageID}`);
      return true;
    } catch (error: any) {
      console.error(`âŒ Postmark Direct API error for ${params.to}:`, error.response?.data || error.message);
      return false;
    }
  }

  // If no Postmark token, simulate
  console.log('ðŸ“§ [SIMULATED EMAIL - No API Token]');
  console.log(`   To: ${params.to}`);
  console.log(`   From: ${params.from}`);
  console.log(`   Subject: ${params.subject}`);
  console.log(`   Has HTML: ${!!params.html}`);
  console.log(`   Has Attachments: ${!!params.attachments?.length}`);
  console.log('   âœ… Email simulated successfully (add POSTMARK_SERVER_TOKEN for real delivery)');
  return true;
}

